'use strict';

var fs = require('fs');
var edmx2csn = require('../edmx2csn')

// read edmx file
let fileName = "testEdmx.xml";
let xml = fs.readFileSync(fileName, 'utf8');

let result = edmx2csn.translateToCsn(xml);

// eslint-disable-next-line no-console
console.log("--------------------------------------------");
// eslint-disable-next-line no-console
console.log(JSON.stringify(result, null, 2));
