/**
 * Thrown in case there is a session instance already but app tries to create another one.
 * @extends {external:Error}
 * @alias module:errors.SessionSingletonError
 */
class SessionSingletonError extends Error {
  /**
   * Create a SessionSingletonError.
   */
  constructor () {
    super('Already connected to a db session')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = SessionSingletonError
