const { Components: { DATA_CREATE_HANDLER } } = require('@sap/odata-v4')
const getContextObject = require('../utils/context-object')
const { toODataResult } = require('../utils/event')
const { validateResourcePathLength } = require('../utils/request')

/**
 * The handler that will be registered with odata-v4.
 * @param {Service} service
 * @param {Object} options
 * @return {Function}
 */
const create = (service, options) => {
  return (req, res, next) => {
    // End here if length is greater then allowed
    validateResourcePathLength(req, options)

    const context = getContextObject(DATA_CREATE_HANDLER, service, req, res)
    const entity = context.target
    const event = entity['@odata.draft.enabled'] ? 'CREATE_DRAFT' : 'CREATE'

    service
      .processEvent(event, context)
      .then(result => {
        next(null, toODataResult(result[0]))
      })
      .catch(err => {
        next(err)
      })
  }
}

module.exports = create
