/**
 * Thrown in case CQN could not be parsed and transformed to SQL.
 * @extends {external:Error}
 * @alias module:errors.CqnParseError
 */
class CqnParseError extends Error {
  /**
   * Create a CqnParseError.
   */
  constructor (rootCauseError) {
    super('CQN could not be parsed and transformed to SQL')
    this.name = this.constructor.name
    this.rootCause = rootCauseError
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = CqnParseError
