'use strict';

const DeserializationError = require('../errors/DeserializationError');

class ReaderErrors {
    static createMoreDataRequired() {
        return new DeserializationError('More data required to process multipart');
    }

    static createExpectingBoundary(cache) {
        return new DeserializationError(`Boundary expected at position ${cache.getReadPos()}`);
    }

    static createIncompleteLastBoundary(cache) {
        return new DeserializationError(
            `Last boundary of multipart must end with '--' at position ${cache.getReadPos()}`);
    }
}

module.exports = ReaderErrors;
