'use strict';

const AbstractError = require('./AbstractError');

/**
 * Client error: the request is not well-formed.
 * @extends AbstractError
 */
class ConfigurationError extends AbstractError {

    /**
     * Creates an instance of ConfigurationError.
     * If an error message is not provided, the default message would be 'Internal server error'.
     *
     * @param {string} message The error message
     * @param {Error} rootCause
     */
    constructor(message = 'Internal server error', rootCause) {
        super(AbstractError.ErrorNames.INTERNAL_SERVER_ERROR, message, rootCause);
    }
}

module.exports = ConfigurationError;
